#include <iostream>#include <utility>using namespace std;struct node{    pair<int,int> pos;    node* next;};void create(node* h,int cnt);int main(int argc,char** argv){    int nodeNum;    cin>>nodeNum;    node* head=new node;    create(head,nodeNum);    return 0;}void create(node* h,int cnt){    if(NULL==h)        return;    node *s,*p=h;    //s=new node;    int x,y;    while(cnt--){        cin>>x>>y;        s=new node;        s->pos=make_pair(x,y);        s->next=NULL;        p->next=s;        p=s;    }}