//the max of continue numbers#include <iostream>#include <string>using namespace std;unsigned int continuMax(char** pOutputstr,char* intputstr);int sort_arr(int* arr,int st,int& flag);int main(){    char* str;    cin>>str;    char** outStr;    unsigned int max_num=continuMax(outStr,str);    /*{        int i=0;        while(outStr[0][i] !='\0')            cout<<outStr[0][i++];        cout<<", "<<i;    }    else        cout<<    */    cout<<","<<max_num;    return 0;}unsigned int continuMax(char** pOutputstr,char* inputstr){    int i=0,j=0,k=0;    //unsigned int j_m,k_m;    int m_tag[100]={0};    if(inputstr[i]==NULL)        return NULL;    while(inputstr[i] != '\0')    {        if('0'<=inputstr[i] && inputstr[i]<='9')            pOutputstr[j][k++]=inputstr[i];        else        {            //wrong here            m_tag[j]=k;            ++j;            k=0;        }        ++i;    }    if(pOutputstr[0][0]==NULL)        return NULL;    int flag=0;    int max_num=sort_arr(m_tag,j,flag);    for(int i=0;i!=max_num;++i)        cout<<pOutputstr[flag][i];    return max_num;   //success}int sort_arr( int *arr,int st,int& flag){    int max=arr[0];    //flag=0;    for(int i=1;i!=st;++i)        if(max<arr[i])        {            max=arr[i];            flag=i;        }    return max;}