/* * learn english * Author: biTree * Date: May 28,2016 */#include <iostream>//#include <map>#include <string>#include <list>using namespace std;string parse(long num);int main(int argc,char** argv){        return 0;}string parse(long num){    string strNum[]={"error","one","two","three","four",        "five","six","seven","eight","night"};    string strBit[]={"hundred","thousand","million","billion"};    string strinTen[]={"error","eleven","twelve","thirteen","fourteen",        "fifteen","sixteen","seventeen","nineteen"};    string strTens[]={"twenty","thirty","forty","fifty",        "sixty","seventy","eighty","nighty"};      list<string> slist;    int val,cnt=1;    int n=num;        do    {        val=n%10;        if(val!=0)        {            if(1==cnt)            {                 slist.push_front(strNum[val]);                ++cnt;            }            if(2==cnt)            {                if(1==val)                {                    //int tmp;                    //if(!svec.empty())                    //{                        int tmp=*(svec.begin());                        slist.clear();                        slist.push_front(strinTen[tmp]);                    //}                    //else                    //    svec.push_back("ten");                }                else                    slist.push_front(strTens[val-2]);/*val is large or equal than 2*/                ++cnt;            }            if(3==cnt)            {                slist.push_front("and");                slist.push_front(strBit[0]);/*hundred*/            }        }        else            ++cnt;    }    while((n/=10) ! = 0);}