#include <iostream>#include <time.h>#include <cstdlib>//#include <WinNT.h>#define VOS_VOID void#define uint unsigned int#define VOS_UINT32 uintusing namespace std;struct node{    int data;    node* next;};void* MLC_MemAlloc(uint uISize);VOS_VOID MLC_MemFree(void* pAddr);VOS_UINT32 MLC_CheckIfMemLeak(uint* puIAddrNum,void* pAddrArry[]);VOS_VOID MLC_CollectMemory(void);int main(int argc,char** argv){    uint uIsize;    cin>>uIsize;    node* head=MLC_MemAlloc(uIsize);    MLC_MemFree(head);    return 0;}void* MLC_MemAlloc(uint uISize){    node *head,*p,*s;    int v,i=0;    srand(time(NULL));    head=new(node);    p=head;    while(i!=uISize){        v=rand()%100;        s=new(node);        s->data=v;        p->next=s;        p=s;        ++i;    }    head=head->next;    p->next=NULL;    return head;}VOS_VOID MLC_MemFree(void* pAddr){    if(NULL==pAddr)        return;    node *p=pAddr;    node *q;    while(true){        if(NULL==p)            break;        q=p->next;        delete p;        p=q;    }    }