#include <iostream>#include <vector>#include <string>#include <algorithm>using namespace std;int main(){    string str;    getline(cin,str);    vector<char> cvec;    string::iterator strIter=str.begin();    for( ; strIter != str.end();++strIter)    {        if('A'<=*strIter<='Z' || 'a'<=*strIter<='z' || '0'<=*strIter<='9')            cvec.push_back(*strIter);    }    sort(cvec.begin(),cvec.end());    vector<char>::iterator vecIter=cvec.begin();    for(;vecIter!=cvec.end();++vecIter)        cout<<*vecIter;    cout<<endl;    return 0;}