/* * Author: biTree * Date: June,12,2016 * 其实结果就是××××××n/2×××××××× */#include <iostream>using namespace std;int main(){    int cnt=0;    int n;    int res=0;    while(cnt++!=10){        cin>>n;        if(0==n)            break;        while(2<=n){            if(0==n%3){                res+=n/3;                n=n/3;                continue;            }            if(1==n%3){                if(1==n)                    break;                res+=n/3;                n=n/3+1;                continue;            }            if(2==n%3){                if(2==n){                    ++res;                    break;                }                res+=n/3;                n=n/3+2;                continue;            }        }        cout<<res<<endl;        res=0;    }    return 0;}