/* * Author: biTree * Date: July 07,2016 * setence reversal sort */#include <iostream>#include <string>using namespace std;string reveWords(string setence);int main(){    string setence;    getline(cin,setence);    string rset=reveWords(setence);    //cout<<rset<<endl;    size_t st=0,pos;    string res;    while(st != rset.size()){        if(isalpha(rset[st])){            pos=st;//point to the first ch in a word            while(isalpha(rset[st]) && st!=rset.size())                ++st;            //cout<<"st="<<st;            string tmp=reveWords(rset.substr(pos,st-pos));            res.append(tmp);            res.append(1,' ');            //cout<<" tmp:"<<tmp;            //rset.replace(pos,st-pos,tmp);            //cout<<"  res: "<<res<<endl;        }        else{            while(!isalpha(rset[st]) && st!=rset.size())                ++st;        }    }    cout<<res<<endl;    return 0;}string reveWords(string setence){    string::iterator be=setence.begin();    string::iterator ed=setence.end()-1;    char tmp;    while(be < ed){        tmp = *be;        *be++ = *ed;        *ed-- = tmp;    }    return setence;}